#if !defined(__SOFT_FP__) && defined(__ARM_FP)
  #warning "FPU is not initialized, but the project is compiling for an FPU. Please initialize the FPU before use."
#endif

#include <stdio.h>
#include <stdint.h>


int main(void)
{
	//LED1 is GPIO Port B 0
	//LED2 is GPIO Port B 7
	//LED3 is GPIO Port B 14
	uint32_t* portB_clock = (uint32_t*)(0x40023800 + 0x30);
	//turn on GPIO ports B clocks
	*portB_clock |= 0x2;
	uint32_t* portB_mode = (uint32_t*)0x40020400;
	//clean relevant bits
	*portB_mode &= 0xCFFF3FFC;
	//set GPIO Port B 0, 7 and 14 to in output mode
	*portB_mode |= 0x10004001;
	uint32_t* portB_output = (uint32_t*)(0x40020400 + 0x14);
	//SET GPIO Port B 0
	*portB_output |= 0x1;
    /* Loop forever */
	while(1){
		for(int i = 0; i<1000000; i++);//Delay
		//Clear GPIO Port B 0
		*portB_output &= ~0x1;
		//SET GPIO Port B 7
		*portB_output |= 0x80;
		for(int i = 0; i<1000000; i++);//Delay
		//Clear GPIO Port B 7
		*portB_output &= ~0x80;
		//SET GPIO Port B 14
		*portB_output |= 0x4000;
		for(int i = 0; i<1000000; i++);//Delay
		//Clear GPIO Port B 14
		*portB_output &= ~0x4000;
		//SET GPIO Port B 0
		*portB_output |= 0x1;
	}
}
